package org.shopping.dao;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.shopping.entity.Order;import org.springframework.stereotype.Repository;import java.util.List;@Repository(value = "ShoppingDao")@Mapperpublic interface OrderDao {    List<Order> findAll();    Order findById(int id);    int add(Order order);    int update(Order order);    int deleteById(int orderId);    int mkOrder(@Param("userId") int userId, @Param("shoppingCarId") int shoppingCarId);}