package org.shopping.dao;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.shopping.entity.ShoppCar;import org.springframework.stereotype.Repository;import java.util.List;@Repository(value = "ShoppCarDao")@Mapperpublic interface ShoppCarDao {    List<ShoppCar> findAll();    ShoppCar findById(int orderId);    int add(ShoppCar shoppCar);    int update(ShoppCar shoppCar);    int deleteById(@Param("shoppCarId") int shoppCarId);    int updateShoppCarNo(@Param("shoppCarId") int shoppCarId,@Param("homeWearNo") int homeWearNo);    void clearCar();    int addCar(@Param("homewearId") int homewearId, @Param("userId") int userId,@Param("price") String price);    int updtaCarNo(@Param("id")int id,@Param("homeWearNo") int homeWearNo);}