package org.shopping.entity;public class Homewear {    private int id;    private String type;    private String name;    private String color;    private String size;    private String price;    private String picture;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public String getSize() {        return size;    }    public void setSize(String size) {        this.size = size;    }    public String getPrice() {        return price;    }    public void setPrice(String price) {        this.price = price;    }    public String getPicture() {        return picture;    }    public void setPicture(String picture) {        this.picture = picture;    }}