package org.shopping.entity;public class Order {    private int orderId;    private int userId;    private int shoppingCarId;    private String shopDate;    private String status;    public int getOrderId() {        return orderId;    }    public void setOrderId(int orderId) {        this.orderId = orderId;    }    public int getUserId() {        return userId;    }    public void setUserId(int userId) {        this.userId = userId;    }    public int getShoppingCarId() {        return shoppingCarId;    }    public void setShoppingCarId(int shoppingCarId) {        this.shoppingCarId = shoppingCarId;    }    public String getShopDate() {        return shopDate;    }    public void setShopDate(String shopDate) {        this.shopDate = shopDate;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }}