package org.shopping.service.impl;import org.shopping.dao.OrderDao;import org.shopping.entity.Order;import org.shopping.service.OrderService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class OrderServiceImpl implements OrderService {    @Autowired    private OrderDao orderDao;    @Override    public List<Order> findAll() {        return orderDao.findAll();    }    @Override    public Order findById(int id) {        return orderDao.findById(id);    }    @Override    public int add(Order order) {        return orderDao.add(order);    }    @Override    public int update(Order order) {        return orderDao.update(order);    }    @Override    public int deleteById(int orderId) {        return orderDao.deleteById(orderId);    }    @Override    public int mkOrder(int userId, int shoppingCarId) {        return orderDao.mkOrder(userId,shoppingCarId);    }}