package org.shopping.service.impl;import org.shopping.dao.ShoppCarDao;import org.shopping.entity.ShoppCar;import org.shopping.service.ShoppCarService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by dell on 2018-04-26. */@Servicepublic class ShoppCarServiceImpl implements ShoppCarService {    @Autowired    private ShoppCarDao shoppingDao;    @Override    public List<ShoppCar> findAll() {        return shoppingDao.findAll();    }    @Override    public ShoppCar findById(int shoppCarId) {        return shoppingDao.findById(shoppCarId);    }    @Override    public int add(ShoppCar shoppCar) {        return shoppingDao.add(shoppCar);    }    @Override    public int update(ShoppCar shoppCar) {        return shoppingDao.update(shoppCar);    }    @Override    public int deleteById(int shoppCarId) {        return shoppingDao.deleteById(shoppCarId);    }    @Override    public int updateShoppCarNo(int shoppCarId, int homeWearNo) {        return shoppingDao.updateShoppCarNo(shoppCarId,homeWearNo);    }    @Override    public void clearCar() {        shoppingDao.clearCar();    }    @Override    public int addCar(int homewearId,int userId, String price) {        return shoppingDao.addCar(homewearId,userId,price);    }    @Override    public int updtaCarNo(int id, int newNo) {        return shoppingDao.updtaCarNo(id,newNo);    }}